const registerUser = (username, email, password, hashedPassword) => {
    if(username.contains(' ')){
        alert("Username cannot contain spaces");
        return false;
    }
    if(username.contains('@')){
        alert("Username cannot contain '@'");
        return false;
    }
    if(username === ''){
        alert("Username cannot be empty");
        return false;
    }
    if(!email.contains('@') || !email.contains('.')){
        alert("Email must contain '@' and '.'");
        return false;
    }
    if(password.length < 8 || !password.contains('@') || !password.contains('.') || !password.contains('!') || !password.contains('#') || !password.contains('$') || !password.contains('%') || !password.contains('^') || !password.contains('&') || !password.contains('*')){
        alert("Password must be at least 8 characters long and contain at least one of the following: '@', '.', '!', '#', '$', '%', '^', '&', '*'");
        return false;
    }
    alert("User registered successfully");
    return true;
};

export {registerUser};
